const AvailableMeals=()=>{
  const [meals, setMeals] = useState([]);
  const[isLoading, setIsLoading] = useState(true)
  useEffect(()=>{    const fetchMeals = async ()=>{
    setIsLoading(true);
    const response = await fetch('https://react-app-1cebe-default-rtdb.firebaseio.com/meals.json')
    console.log(response,"fgggggggggggggggggggggggggggg")
  const responseData = await response.json();
  const loadedMeals = [];
  for (const key in responseData){
    loadedMeals.push({
      id: key,
      name: responseData[key].name,
      description: responseData[key].description,
      price: responseData[key].price
    })
  }
  setMeals(loadedMeals);
  setIsLoading(false);
  };

  fetchMeals();
},[]);

if(isLoading){
  return<section classes = {classes.Meal}>
    <p>Loading...</p>
  </section>
}

  const mealsList = meals.map(meal=>(
  <MealItem 
  id  = {meal.id}
  key={meal.id} 
  name = {meal.name} 
  description = {meal.description} 
  price = { meal.price}
  />
  ));
  return(
  <section className={classes.meals}>
    <Card><ul>
      {mealsList}
    </ul></Card>
  </section>
  )}
export default AvailableMeals;
